import React,{useContext,useState} from 'react'
import "./Login.css";
import { Link, Redirect } from "react-router-dom";
const Login=()=>{
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [currentView, setCurrentview] = useState("signUp");

  Loggedin=async()=>{
     const res=await fetch("/loggedInData",{
      method:"POST",
      headers:{
        "Accept":"application/json",
        "Content-Type":"application/json"
      },
      body:JSON.stringify({
          name:this.state.name,
          email:this.state.email,
          password:this.state.password,
      })
    })
    const data=await res.json();
    console.log(data);
  }

  loginApprove=async()=>{
    
    let ans=await fetch("/usersData")
    .then(function (res) {
      return res.json();
    })
    .then((json) => {
      return json;
    });
    let x=false;
    ans.forEach((ele)=>{
      console.log(this.state.email);
        console.log(ele.email);
        console.log(this.state.password);
        console.log(ele.password);
      if(this.state.email==ele.email && this.state.password===ele.password)
      {
        return true;
      }
    })
    return false;
    console.log(this.props);
    

  }

  currentView = () => {
    switch(this.state.currentView) {
      case "signUp":
        return (
          <div className="formclass">
            <h2>Sign Up!</h2>
            <fieldset>
              <legend>Create Account</legend>
              <ul>
                <li>
                  <label for="username">Username:</label>
                  <input  type="text" id="username" onChange={ (el) => setName(el.target.value)} required/>
                </li>
                <li>
                  <label for="email">Email:</label>
                  <input type="email" id="email" onChange={ (el) => setEmail(el.target.value)} required/>
                </li>
                <li>
                  <label for="password">Password:</label>
                  <input  type="password" id="password" onChange={ (el) => setPassword(el.target.value)} required/>
                </li>
              </ul>
            </fieldset>
            
            <Link to={{pathname:"/Myntra"}}>
              <button className="btn" onClick={ () => Loggedin()}>Submit</button>
            </Link>
            <button  onClick={ () => setCurrentview("signUp")}>Have an Account?</button>
          </div>
        )
        break
      case "logIn":
        return (
          <div className="formclass">
            <h2>Welcome Back!</h2>
            <fieldset>
              <legend>Log In</legend>
              <ul>
                <li>
                  <label for="email">Email:</label>
                  <input  type="text" id="email" onChange={ (el) => setEmail(el.target.value)} required/>
                </li>
                <li>
                  <label for="password">Password:</label>
                  <input type="password" id="password" onChange={ (el) => setPassword(el.target.value)} required/>
                </li>
                <li>
                  <i/>
                  <a onClick={ () => setCurrentview("signUp")}>Forgot Password?</a>
                </li>
              </ul>
            </fieldset>
           
            <button className="btn" onClick={ () => loginApprove()}>Login</button>
            <button onClick={ () => setCurrentview("signUp")}>Create an Account</button>
          </div>
        )
        break;
      case "PWReset":
        return (
          <div className="formclass">
          <h2>Reset Password</h2>
          <fieldset>
            <legend>Password Reset</legend>
            <ul>
              <li>
                <em>A reset link will be sent to your inbox!</em>
              </li>
              <li>
                <label for="email">Email:</label>
                <input type="email" id="email" required/>
              </li>
            </ul>
          </fieldset>
          <button>Send Reset Link</button>
          <button type="submit" onClick={ () => setCurrentview("logIn")}>Go Back</button>
        </div>
        )
      default:
        break
    }
  }

  return (
    <section id="entry-page">
      {currentView()}
    </section>
  )
}
export default Login;
