import React from "react"
import "./Login.css";
import { Link } from "react-router-dom";
class Login extends React.Component {
  state={
    name:"",
    email:"",
    password:"",
    currentView:"signUp"
  }
  Loggedin=async()=>{
    console.log({
      name:this.state.name,
      email:this.state.email,
      password:this.state.password,
  });
    const res=await fetch("/loggedInData",{
      mehtond:"POST",
      headers:{
        "Content-Type":"application/json"
      },
      body:JSON.stringify({
          name:this.state.name,
          email:this.state.email,
          password:this.state.password,
      })
    })
  }

  changeView = (view) => {
    this.setState({
      uid:this.state.uid,
      name:this.state.name,
      email:this.state.email,
      password:this.state.password,
      currentView: view
    })
  }
  setUserName =(el)=>{
    this.setState({
      uid:this.state.uid,
      name:el,
      email:this.state.email,
      password:this.state.password,
      currentView: this.state.currentView
    })
  }
  setEmail =(el)=>{
    this.setState({
      uid:this.state.uid,
      name:this.state.name,
      email:el,
      password:this.state.password,
      currentView: this.state.currentView
    })
  }
  setPassword=(el)=>{
    this.setState({
      uid:this.state.uid,
      name:this.state.name,
      email:this.state.email,
      password:el,
      currentView: this.state.currentView
    })
  }
  currentView = () => {
    switch(this.state.currentView) {
      case "signUp":
        return (
          <form className="formclass">
            <h2>Sign Up!</h2>
            <fieldset>
              <legend>Create Account</legend>
              <ul>
                <li>
                  <label for="username">Username:</label>
                  <input type="text" id="username" onChange={ (el) => this.setUserName(el)} required/>
                </li>
                <li>
                  <label for="email">Email:</label>
                  <input type="email" id="email" onChange={ (el) => this.setEmail(el)} required/>
                </li>
                <li>
                  <label for="password">Password:</label>
                  <input type="password" id="password" onChange={ (el) => this.setPassword(el)} required/>
                </li>
              </ul>
            </fieldset>
            
            <Link to={{pathname:"/Myntra"}}>
              <button className="btn" onClick={ () => this.Loggedin()}>Submit</button>
            </Link>
            <button type="button"  className="button" onClick={ () => this.changeView("logIn")}>Have an Account?</button>
          </form>
        )
        break
      case "logIn":
        return (
          <form className="formclass">
            <h2>Welcome Back!</h2>
            <fieldset>
              <legend>Log In</legend>
              <ul>
                <li>
                  <label for="email">Email:</label>
                  <input type="text" id="email" onChange={ (el) => this.setEmail(el)} required/>
                </li>
                <li>
                  <label for="password">Password:</label>
                  <input type="password" id="password" onChange={ (el) => this.setPassword(el)} required/>
                </li>
                <li>
                  <i/>
                  <a onClick={ () => this.changeView("PWReset")}>Forgot Password?</a>
                </li>
              </ul>
            </fieldset>
            <Link to={{pathname:"/Myntra"}}>
              <button className="btn">Login</button>
            </Link>
            <button onClick={ () => this.changeView("signUp")}>Create an Account</button>
          </form>
        )
        break
      case "PWReset":
        return (
          <form className="formclass">
          <h2>Reset Password</h2>
          <fieldset>
            <legend>Password Reset</legend>
            <ul>
              <li>
                <em>A reset link will be sent to your inbox!</em>
              </li>
              <li>
                <label for="email">Email:</label>
                <input type="email" id="email" required/>
              </li>
            </ul>
          </fieldset>
          <button>Send Reset Link</button>
          <button type="button" onClick={ () => this.changeView("logIn")}>Go Back</button>
        </form>
        )
      default:
        break
    }
  }


  render() {
    return (
      <section id="entry-page">
        {this.currentView()}
      </section>
    )
  }
}
export default Login;
