import React from "react"
import "./Login.css";
import { Link, Redirect } from "react-router-dom";
const Login=()=>{
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [currentView, setCurrentview] = useState("signUp");
  Loggedin=async()=>{
     const res=await fetch("/loggedInData",{
      method:"POST",
      headers:{
        "Accept":"application/json",
        "Content-Type":"application/json"
      },
      body:JSON.stringify({
          name:this.state.name,
          email:this.state.email,
          password:this.state.password,
      })
    })
    const data=await res.json();
    console.log(data);
  }
  loginApprove=async()=>{
    
    let ans=await fetch("/usersData")
    .then(function (res) {
      return res.json();
    })
    .then((json) => {
      return json;
    });
    let x=false;
    ans.forEach((ele)=>{
      console.log(this.state.email);
        console.log(ele.email);
        console.log(this.state.password);
        console.log(ele.password);
      if(this.state.email==ele.email && this.state.password===ele.password)
      {
        return true;
      }
    })
    return false;
    console.log(this.props);
    

  }

  changeView = (view) => {
    this.setState({
      ...this.state,currentView: view
    })
  }
  setUserName =(el)=>{
    
  }
  setEmail =(el)=>{
    setemail(el.target.value);
  }
  setpassword=(el)=>{
    setPassword(el.target.value);
  }
  currentView = () => {
    switch(this.state.currentView) {
      case "signUp":
        return (
          <div className="formclass">
            <h2>Sign Up!</h2>
            <fieldset>
              <legend>Create Account</legend>
              <ul>
                <li>
                  <label for="username">Username:</label>
                  <input  type="text" id="username" onChange={ (el) => this.setUserName(el)} required/>
                </li>
                <li>
                  <label for="email">Email:</label>
                  <input type="email" id="email" onChange={ (el) => this.setEmail(el)} required/>
                </li>
                <li>
                  <label for="password">Password:</label>
                  <input  type="password" id="password" onChange={ (el) => this.setPassword(el)} required/>
                </li>
              </ul>
            </fieldset>
            
            <Link to={{pathname:"/Myntra"}}>
              <button className="btn" onClick={ () => this.Loggedin()}>Submit</button>
            </Link>
            <button  onClick={ () => this.changeView("logIn")}>Have an Account?</button>
          </div>
        )
        break
      case "logIn":
        return (
          <div className="formclass">
            <h2>Welcome Back!</h2>
            <fieldset>
              <legend>Log In</legend>
              <ul>
                <li>
                  <label for="email">Email:</label>
                  <input  type="text" id="email" onChange={ (el) => this.setEmail(el)} required/>
                </li>
                <li>
                  <label for="password">Password:</label>
                  <input type="password" id="password" onChange={ (el) => this.setPassword(el)} required/>
                </li>
                <li>
                  <i/>
                  <a onClick={ () => this.changeView("PWReset")}>Forgot Password?</a>
                </li>
              </ul>
            </fieldset>
           
            <button className="btn" onClick={ () => this.loginApprove()}>Login</button>
            <button onClick={ () => this.changeView("signUp")}>Create an Account</button>
          </div>
        )
        break;
      case "PWReset":
        return (
          <div className="formclass">
          <h2>Reset Password</h2>
          <fieldset>
            <legend>Password Reset</legend>
            <ul>
              <li>
                <em>A reset link will be sent to your inbox!</em>
              </li>
              <li>
                <label for="email">Email:</label>
                <input type="email" id="email" required/>
              </li>
            </ul>
          </fieldset>
          <button>Send Reset Link</button>
          <button type="submit" onClick={ () => this.changeView("logIn")}>Go Back</button>
        </div>
        )
      default:
        break
    }
  }

  return (
    <section id="entry-page">
      {this.currentView()}
    </section>
  )
}
export default Login;
